class WebinarApiService {
  constructor() {
    this.baseUrl = process.env.REACT_APP_FUNCTIONS_URL || 'http://localhost:5001/your-project/us-central1';
  }

  async createRazorpayOrder(orderData) {
    const resp = await fetch(`${this.baseUrl}/createOrder`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(orderData),
    });
    if (!resp.ok) throw new Error('Failed to create order');
    return resp.json();
  }

  async verifyRazorpayPayment(paymentData) {
    const resp = await fetch(`${this.baseUrl}/verifyPayment`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(paymentData),
    });
    if (!resp.ok) throw new Error('Verification failed');
    return resp.json();
  }

  // Legacy methods for backward compatibility
  async createOrder({ baseUrl, registrationId, amount }) {
    const resp = await fetch(`${baseUrl}/createOrder`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ registrationId, amount }),
    });
    if (!resp.ok) throw new Error('Failed to create order');
    return resp.json();
  }

  async verifyPayment({ baseUrl, registrationId, razorpay_order_id, razorpay_payment_id, razorpay_signature }) {
    const resp = await fetch(`${baseUrl}/verifyPayment`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ registrationId, razorpay_order_id, razorpay_payment_id, razorpay_signature }),
    });
    if (!resp.ok) throw new Error('Verification failed');
    return resp.json();
  }
}

export const webinarApi = new WebinarApiService();
