import React from 'react';
import { useInView } from 'react-intersection-observer';
import { useAuth } from '../contexts/AuthContext';
import { analyticsService } from '../services/analytics';

const Pricing = ({ showNotification }) => {
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true
  });

  const pricingTiers = [
    {
      name: 'Vibe Explorer',
      price: 29,
      period: '/month',
      features: [
        'Core lifestyle modules',
        'Community access',
        'Monthly vibe sessions',
        'Basic resources'
      ],
      buttonText: 'Start Exploring',
      featured: false
    },
    {
      name: 'Vibe Master',
      price: 79,
      period: '/month',
      features: [
        'All Explorer features',
        'Advanced lifestyle coaching',
        'Personal vibe mentor',
        'Weekly live sessions',
        'Lifestyle certification'
      ],
      buttonText: 'Become a Master',
      featured: true,
      badge: 'Lifestyle Choice'
    },
    {
      name: 'Vibe Legend',
      price: 149,
      period: '/month',
      features: [
        'All Master features',
        'Custom lifestyle plan',
        'Global community leadership',
        'Exclusive events access',
        'Brand partnership opportunities'
      ],
      buttonText: 'Become a Legend',
      featured: false
    }
  ];

  const handlePricingClick = (tierName) => {
    showNotification(`Redirecting to ${tierName} plan...`, 'info');

    setTimeout(() => {
      showNotification('Payment integration coming soon!', 'info');
    }, 1000);
  };

  return (
    <section id="pricing" className="pricing">
      <div className="container">
        <h2 className="section-title">Choose Your Lifestyle Tier</h2>
        <div className="pricing-grid" ref={ref}>
          {pricingTiers.map((tier, index) => (
            <div 
              key={index}
              className={`pricing-card ${tier.featured ? 'featured' : ''}`}
              style={{
                opacity: inView ? 1 : 0,
                transform: inView ? 'translateY(0)' : 'translateY(30px)',
                transition: `opacity 0.6s ease ${index * 0.1}s, transform 0.6s ease ${index * 0.1}s`
              }}
            >
              {tier.badge && (
                <div className="pricing-badge">{tier.badge}</div>
              )}
              <div className="pricing-header">
                <h3>{tier.name}</h3>
                <div className="price">
                  <span className="currency">$</span>
                  <span className="amount">{tier.price}</span>
                  <span className="period">{tier.period}</span>
                </div>
              </div>
              <ul className="pricing-features">
                {tier.features.map((feature, featureIndex) => (
                  <li key={featureIndex}>
                    <i className="fas fa-check"></i>
                    {feature}
                  </li>
                ))}
              </ul>
              <button 
                className={`btn ${tier.featured ? 'btn-primary' : 'btn-outline'}`}
                onClick={() => handlePricingClick(tier.name)}
              >
                {tier.buttonText}
              </button>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Pricing;
