import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createWebinarRegistration } from '../services/webinar';
import { openRazorpayCheckout } from '../services/razorpay';
import { webinarApi } from '../services/webinarApi';

const WebinarRegister = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({ name: '', email: '', phone: '' });
  const [submitting, setSubmitting] = useState(false);
  const serverBaseUrl = process.env.REACT_APP_SERVER_URL || 'http://localhost:5000';

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      const { id } = await createWebinarRegistration({ ...form, amount: 99, currency: 'INR' });

      // Create order via local server
      const response = await fetch(`${serverBaseUrl}/createWebinarOrder`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          amount: 99,
          name: form.name,
          email: form.email,
          phone: form.phone,
          receipt: `webinar_${id}`
        })
      });

      if (!response.ok) {
        throw new Error('Failed to create order');
      }

      const { order } = await response.json();

      await openRazorpayCheckout({
        name: form.name,
        email: form.email,
        phone: form.phone,
        amount: 99,
        onSuccess: async (resp) => {
          const result = await webinarApi.verifyPayment({
            baseUrl: functionsBaseUrl,
            registrationId: id,
            razorpay_order_id: resp.razorpay_order_id,
            razorpay_payment_id: resp.razorpay_payment_id,
            razorpay_signature: resp.razorpay_signature,
          });
          if (result.success) {
            navigate('/webinar/success?ref=' + id);
          } else {
            alert('Payment signature verification failed.');
          }
        },
        onFailure: async () => {
          alert('Payment failed. Please try again.');
        },
      });
    } catch (err) {
      console.error(err);
      alert('Registration failed. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <section className="webinar-register">
      <div className="container">
        <h1 className="section-title">Register for Vibe Coding Webinar</h1>
        <p style={{ textAlign: 'center', marginBottom: '1rem' }}>22 September, 7:00 PM IST â€¢ Fee: â‚¹99</p>
        <form className="contact-form" onSubmit={onSubmit}>
          <div className="form-group">
            <input name="name" value={form.name} onChange={handleChange} type="text" placeholder="Your Name" required />
          </div>
          <div className="form-group">
            <input name="email" value={form.email} onChange={handleChange} type="email" placeholder="Your Email" required />
          </div>
          <div className="form-group">
            <input name="phone" value={form.phone} onChange={handleChange} type="tel" placeholder="Phone (WhatsApp preferred)" required />
          </div>
          <button disabled={submitting} type="submit" className="btn btn-primary">
            {submitting ? 'Processing...' : 'Pay â‚¹99 & Register'}
          </button>
        </form>
        <p style={{ textAlign: 'center', marginTop: '1rem', color: '#666' }}>
          Secure payment via Razorpay. Confirmation sent to your email.
        </p>
      </div>
    </section>
  );
};

export default WebinarRegister;
