import React, { useState, useEffect } from 'react';
import { subscriptionService } from '../services/subscriptions';
import { useAuth } from '../contexts/AuthContext';

const SubscriptionManager = ({ user, showNotification, onSubscriptionChange }) => {
  const [subscription, setSubscription] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (user) {
      loadUserSubscription();
    }
  }, [user]);

  const loadUserSubscription = async () => {
    try {
      const userSubscriptions = await localDatabase.getUserSubscriptions(user.id);
      if (userSubscriptions.length > 0) {
        setSubscription(userSubscriptions[0]); // Get the most recent subscription
      }
      setIsLoading(false);
    } catch (error) {
      console.error('Error loading subscription:', error);
      setIsLoading(false);
    }
  };

  const handleSubscribe = async (tier) => {
    try {
      const newSubscription = await localDatabase.createSubscription({
        userId: user.id,
        tier: tier.name,
        price: tier.price,
        status: 'active',
        startDate: new Date().toISOString(),
        features: tier.features
      });

      setSubscription(newSubscription);
      showNotification(`Successfully subscribed to ${tier.name}!`, 'success');

      if (onSubscriptionChange) {
        onSubscriptionChange(newSubscription);
      }
    } catch (error) {
      console.error('Error creating subscription:', error);
      showNotification('Failed to create subscription. Please try again.', 'error');
    }
  };

  const handleCancelSubscription = async () => {
    try {
      await localDatabase.cancelSubscription(subscription.id);
      setSubscription(null);
      showNotification('Subscription cancelled successfully.', 'info');

      if (onSubscriptionChange) {
        onSubscriptionChange(null);
      }
    } catch (error) {
      console.error('Error cancelling subscription:', error);
      showNotification('Failed to cancel subscription. Please try again.', 'error');
    }
  };

  const subscriptionTiers = [
    {
      name: 'Vibe Explorer',
      price: 29,
      period: 'month',
      features: [
        'Access to Vibe Coding Foundations course',
        'Community forum access',
        'Monthly vibe sessions',
        'Basic resources and templates'
      ],
      color: '#667eea'
    },
    {
      name: 'Vibe Master',
      price: 79,
      period: 'month',
      features: [
        'All Explorer features',
        'Access to all 4 courses',
        'Personal vibe mentor',
        'Weekly live sessions',
        'Lifestyle certification',
        'Priority support'
      ],
      color: '#4ecdc4',
      featured: true
    },
    {
      name: 'Vibe Legend',
      price: 149,
      period: 'month',
      features: [
        'All Master features',
        'Custom lifestyle plan',
        'Global community leadership',
        'Exclusive events access',
        'Brand partnership opportunities',
        '1-on-1 coaching sessions'
      ],
      color: '#ff6b6b'
    }
  ];

  const getAccessLevel = () => {
    if (!subscription) return 'none';

    switch (subscription.tier) {
      case 'Vibe Explorer':
        return 'foundations';
      case 'Vibe Master':
        return 'all';
      case 'Vibe Legend':
        return 'all';
      default:
        return 'none';
    }
  };

  const hasCourseAccess = (courseId) => {
    const accessLevel = getAccessLevel();

    if (accessLevel === 'all') return true;
    if (accessLevel === 'foundations' && courseId === 'vibe-foundations') return true;

    return false;
  };

  if (isLoading) {
    return (
      <div className="subscription-manager">
        <div className="container">
          <div className="loading">Loading subscription...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="subscription-manager">
      <div className="container">
        <div className="subscription-header">
          <h2>Your Subscription</h2>
          <p>Manage your vibe coding journey access</p>
        </div>

        {subscription ? (
          <div className="current-subscription">
            <div className="subscription-card active">
              <div className="subscription-info">
                <h3>{subscription.tier}</h3>
                <p className="price">${subscription.price}/month</p>
                <p className="status">Active since {new Date(subscription.startDate).toLocaleDateString()}</p>
              </div>
              <div className="subscription-features">
                <h4>Your Access:</h4>
                <ul>
                  {subscription.features.map((feature, index) => (
                    <li key={index}>
                      <i className="fas fa-check"></i>
                      {feature}
                    </li>
                  ))}
                </ul>
              </div>
              <div className="subscription-actions">
                <button 
                  className="btn btn-outline"
                  onClick={handleCancelSubscription}
                >
                  Cancel Subscription
                </button>
              </div>
            </div>
          </div>
        ) : (
          <div className="subscription-options">
            <div className="subscription-grid">
              {subscriptionTiers.map((tier, index) => (
                <div 
                  key={index}
                  className={`subscription-card ${tier.featured ? 'featured' : ''}`}
                  style={{ borderColor: tier.color }}
                >
                  {tier.featured && (
                    <div className="featured-badge" style={{ backgroundColor: tier.color }}>
                      Most Popular
                    </div>
                  )}
                  <div className="tier-header">
                    <h3>{tier.name}</h3>
                    <div className="price">
                      <span className="currency">$</span>
                      <span className="amount">{tier.price}</span>
                      <span className="period">/{tier.period}</span>
                    </div>
                  </div>
                  <div className="tier-features">
                    <ul>
                      {tier.features.map((feature, featureIndex) => (
                        <li key={featureIndex}>
                          <i className="fas fa-check"></i>
                          {feature}
                        </li>
                      ))}
                    </ul>
                  </div>
                  <button 
                    className="btn btn-primary"
                    onClick={() => handleSubscribe(tier)}
                    style={{ backgroundColor: tier.color }}
                  >
                    Subscribe Now
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        <div className="course-access">
          <h3>Course Access</h3>
          <div className="access-grid">
            <div className={`access-card ${hasCourseAccess('vibe-foundations') ? 'active' : 'locked'}`}>
              <div className="access-icon">🎨</div>
              <h4>Vibe Coding Foundations</h4>
              <p>Master the fundamentals of creative programming</p>
              {hasCourseAccess('vibe-foundations') ? (
