import React from 'react';
import { useInView } from 'react-intersection-observer';
import { useAuth } from '../contexts/AuthContext';
import { analyticsService } from '../services/analytics';

const Pricing = ({ showNotification }) => {
  const { currentUser } = useAuth();
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true
  });

  const pricingTiers = [
    {
      name: 'Vibe Explorer',
      price: 29,
      period: '/month',
      features: [
        'Core lifestyle modules',
        'Community access',
        'Monthly vibe sessions',
        'Basic resources'
      ],
      buttonText: 'Start Exploring',
      featured: false
    },
    {
      name: 'Vibe Master',
      price: 79,
      period: '/month',
      features: [
        'All Explorer features',
        'Advanced lifestyle coaching',
        'Personal vibe mentor',
        'Weekly live sessions',
        'Lifestyle certification'
      ],
      buttonText: 'Become a Master',
      featured: true,
      badge: 'Lifestyle Choice'
    },
    {
      name: 'Vibe Legend',
      price: 149,
      period: '/month',
      features: [
        'All Master features',
        'Custom lifestyle plan',
        'Global community leadership',
        'Exclusive events access',
        'Brand partnership opportunities'
      ],
      buttonText: 'Become a Legend',
      featured: false
    }
  ];

  const handlePricingClick = async (tier) => {
    // Track pricing button click
    await analyticsService.trackEvent('pricing_button_clicked', {
      tier: tier.name,
      price: tier.price,
      userLoggedIn: !!currentUser
    });

    if (!currentUser) {
      showNotification('Please login to subscribe to a plan', 'info');
      // Trigger login modal - you might want to pass a callback to open login modal
      return;
    }

    try {
      // Import Razorpay dynamically
      const { openSubscriptionCheckout } = await import('../services/razorpay');

      const orderData = {
        amount: tier.price * 100, // Convert to paise
        currency: 'INR',
        receipt: `sub_${tier.name.toLowerCase().replace(/\s+/g, '_')}_${Date.now()}`,
        notes: {
          tier: tier.name,
          userId: currentUser.uid,
          userEmail: currentUser.email
        }
      };

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount: orderData.amount,
        currency: orderData.currency,
        name: 'Vibe Coding Lifestyle',
        description: `${tier.name} Subscription`,
        order_id: '', // Will be set after order creation
        handler: async (response) => {
          try {
            // Import subscription service dynamically
            const { subscriptionService } = await import('../services/subscriptions');

            // Create subscription in Firestore after successful payment
            const newSubscription = await subscriptionService.createSubscription({
              userId: currentUser.uid,
              tier: tier.name,
              price: tier.price,
              status: 'active',
              startDate: new Date().toISOString(),
              features: tier.features,
              paymentId: response.razorpay_payment_id,
              orderId: response.razorpay_order_id,
              signature: response.razorpay_signature
            });

            showNotification(`Successfully subscribed to ${tier.name}!`, 'success');

            // Track successful subscription
            await analyticsService.trackEvent('subscription_completed', {
              tier: tier.name,
              price: tier.price,
              paymentId: response.razorpay_payment_id
            });
          } catch (error) {
            console.error('Error creating subscription after payment:', error);
            showNotification('Payment successful but subscription creation failed. Please contact support.', 'error');
          }
        },
        prefill: {
          name: currentUser.displayName || '',
          email: currentUser.email,
        },
        theme: {
          color: '#667eea'
        }
      };

      await openSubscriptionCheckout(orderData, options);
    } catch (error) {
      console.error('Error initiating subscription payment:', error);
      showNotification('Failed to initiate payment. Please try again.', 'error');
    }
  };

  return (
    <section id="pricing" className="pricing">
      <div className="container">
        <h2 className="section-title">Choose Your Lifestyle Tier</h2>
        <div className="pricing-grid" ref={ref}>
          {pricingTiers.map((tier, index) => (
            <div 
              key={index}
              className={`pricing-card ${tier.featured ? 'featured' : ''}`}
              style={{
                opacity: inView ? 1 : 0,
                transform: inView ? 'translateY(0)' : 'translateY(30px)',
                transition: `opacity 0.6s ease ${index * 0.1}s, transform 0.6s ease ${index * 0.1}s`
              }}
            >
              {tier.badge && (
                <div className="pricing-badge">{tier.badge}</div>
              )}
              <div className="pricing-header">
                <h3>{tier.name}</h3>
                <div className="price">
                  <span className="currency">$</span>
                  <span className="amount">{tier.price}</span>
                  <span className="period">{tier.period}</span>
                </div>
              </div>
              <ul className="pricing-features">
                {tier.features.map((feature, featureIndex) => (
                  <li key={featureIndex}>
                    <i className="fas fa-check"></i>
                    {feature}
                  </li>
                ))}
              </ul>
              <button 
                className={`btn ${tier.featured ? 'btn-primary' : 'btn-outline'}`}
                onClick={() => handlePricingClick(tier)}
              >
                {tier.buttonText}
              </button>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Pricing;
