import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';

const LoginModal = ({ isOpen, onClose, onLogin, showNotification }) => {
  const { signInWithGoogle, emailSignIn, emailSignUp } = useAuth();
  const [isSignUp, setIsSignUp] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: ''
  });
  const [submitting, setSubmitting] = useState(false);

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleEmailAuth = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      let user;
      if (isSignUp) {
        user = await emailSignUp({
          name: formData.name,
          email: formData.email,
          password: formData.password,
        });
        showNotification('Account created successfully!', 'success');
      } else {
        user = await emailSignIn(formData.email, formData.password);
        showNotification('Welcome back!', 'success');
      }
      onLogin?.(user);
      onClose();
    } catch (error) {
      console.error('Auth error:', error);
      showNotification(error.message || 'Authentication failed', 'error');
    } finally {
      setSubmitting(false);
    }
  };

  const handleGoogle = async () => {
    setSubmitting(true);
    try {
      const user = await signInWithGoogle();
      showNotification('Signed in with Google!', 'success');
      onLogin?.(user);
      onClose();
    } catch (error) {
      console.error('Google auth error:', error);
      showNotification(error.message || 'Google sign-in failed', 'error');
    } finally {
      setSubmitting(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>{isSignUp ? 'Create Account' : 'Welcome Back'}</h2>
          <button className="close-btn" onClick={onClose}>
            <i className="fas fa-times"></i>
          </button>
        </div>

        <form onSubmit={handleEmailAuth} className="auth-form">
          {isSignUp && (
            <div className="form-group">
              <label htmlFor="name">Full Name</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
                placeholder="Enter your full name"
              />
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
              placeholder="Enter your email"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              required
              placeholder="Enter your password"
            />
          </div>

          <button type="submit" className="btn btn-primary" disabled={submitting}>
            {submitting ? (isSignUp ? 'Creating...' : 'Signing in...') : (isSignUp ? 'Create Account' : 'Sign In')}
          </button>
        </form>

        <div className="auth-switch">
          <p>
            {isSignUp ? 'Already have an account?' : "Don't have an account?"}
            <button 
              className="switch-btn"
              onClick={() => setIsSignUp(!isSignUp)}
            >
              {isSignUp ? 'Sign In' : 'Create Account'}
            </button>
          </p>
          <div style={{ marginTop: '0.75rem' }}>
            <button className="btn btn-outline" onClick={handleGoogle} disabled={submitting}>
              <i className="fab fa-google" style={{ marginRight: '8px' }} /> Continue with Google
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginModal;
