import { initializeApp } from 'firebase/app';
import { getFirestore } from 'firebase/firestore';
import { getAnalytics, isSupported } from 'firebase/analytics';

// Static fallback config (current project)
const fallbackConfig = {
  apiKey: 'AIzaSyDe671PgpCIqewl4iLXcWEUs1oWL3jlzp0',
  authDomain: 'rraasi.firebaseapp.com',
  projectId: 'rraasi',
  storageBucket: 'rraasi.firebasestorage.app',
  messagingSenderId: '764676936249',
  appId: '1:764676936249:web:e17257693e292a2d0873e8',
  measurementId: 'G-2M5RSRFL4S',
};

// Env-based override (set these to switch to your 'vibecoding' project)
const envConfig = {
  apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
  authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN,
  projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID,
  storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET,
  messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
  appId: process.env.REACT_APP_FIREBASE_APP_ID,
  measurementId: process.env.REACT_APP_FIREBASE_MEASUREMENT_ID,
};

const hasEnvConfig = Boolean(envConfig.apiKey && envConfig.projectId && envConfig.appId);
const firebaseConfig = hasEnvConfig ? envConfig : fallbackConfig;

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

// Initialize Analytics only when supported (browser env)
let analytics = null;
try {
  isSupported().then((supported) => {
    if (supported) {
      analytics = getAnalytics(app);
    }
  });
} catch (_) {
  // analytics optional
}

export { app as default, db, analytics };
