    const data = this.getData();
    const newContact = {
      id: Date.now().toString(),
      ...formData,
      timestamp: new Date().toISOString(),
      status: 'new'
    };

    data.contacts.push(newContact);
    data.analytics.formSubmissions += 1;

    this.saveData(data);
    return newContact;
  }

  // User registration
  async registerUser(userData) {
    const data = this.getData();
    const newUser = {
      id: Date.now().toString(),
      ...userData,
      createdAt: new Date().toISOString(),
      status: 'active'
    };

    data.users.push(newUser);
    this.saveData(data);
    return newUser;
  }

  // Subscription management
  async createSubscription(subscriptionData) {
    const data = this.getData();
    const newSubscription = {
      id: Date.now().toString(),
      ...subscriptionData,
      createdAt: new Date().toISOString(),
      status: 'pending'
    };

    data.subscriptions.push(newSubscription);
    this.saveData(data);
    return newSubscription;
  }

  // Analytics tracking
  async trackEvent(eventType, eventData = {}) {
    const data = this.getData();

    switch (eventType) {
      case 'pageView':
        data.analytics.pageViews += 1;
        break;
      case 'buttonClick':
        data.analytics.buttonClicks += 1;
        break;
      case 'formSubmission':
        data.analytics.formSubmissions += 1;
        break;
      default:
        // Handle custom events
        if (!data.analytics[eventType]) {
          data.analytics[eventType] = 0;
        }
        data.analytics[eventType] += 1;
    }

    this.saveData(data);
  }

  // Get analytics
  async getAnalytics() {
    const data = this.getData();
    return data.analytics || {};
  }

  // Get contacts
  async getContacts() {
    const data = this.getData();
    return data.contacts || [];
  }

  // Get users
  async getUsers() {
    const data = this.getData();
    return data.users || [];
  }

  // Get subscriptions
  async getSubscriptions() {
    const data = this.getData();
    return data.subscriptions || [];
  }

  // Search functionality
  async searchContacts(query) {
    const contacts = await this.getContacts();
    return contacts.filter(contact => 
      contact.name?.toLowerCase().includes(query.toLowerCase()) ||
      contact.email?.toLowerCase().includes(query.toLowerCase()) ||
      contact.message?.toLowerCase().includes(query.toLowerCase())
    );
  }

  // Export data (useful for migration to Firebase)
  exportData() {
    return this.getData();
  }

  // Import data (useful for migration from Firebase)
  importData(data) {
    return this.saveData(data);
  }

  // Clear all data (for testing)
  clearData() {
    localStorage.removeItem(this.storageKey);
    this.initializeLocalStorage();
  }
}

